import React from 'react';
import axios from 'axios';
import qs from 'qs';
import { sha1 } from '../helper/sha1.js';

class LoginPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            submitted: false,
            loading: false,
            error: ''
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        var { username, password, returnUrl } = this.state;

        // stop here if form is invalid
        if (!(username && password)) {
            return;
        }

        this.setState({ loading: true });

        let config = {
            headers: {
                "Content-Type" : "application/x-www-form-urlencoded",
            },
        }

        // sha1 encode password
        password = sha1(password);

        // use axios to verify password
        axios.post('/api/auth/signin',qs.stringify({username, password}), config).then((data)=>{
            console.log("Authenticated!");

            localStorage.setItem("user", username);
            // redirect to home page
            this.props.history.push('/main');
        }).catch(()=>{
            alert("error")
        })
    }

    render() {
        const { username, password, submitted, loading, error } = this.state;
        return (
            <div class="container">
            <div className="col-md-6 col-md-offset-3" style={{marginTop: 100}}>
                <div className="alert alert-info">
                    Username: P176100<br />
                    Password: 10086
                </div>
                <h2>Login</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <label htmlFor="username">Username</label>
                        <input type="text" className="form-control" name="username" value={username} onChange={this.handleChange} />
                        {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary" disabled={loading}>Login</button>
                        {loading &&
                            <img src="data:image/gif;base64," />
                        }
                    </div>
                    {error &&
                        <div className={'alert alert-danger'}>{error}</div>
                    }
                </form>
            </div>
            </div>
        );
    }
}

export default LoginPage